import React from 'react';
import firebase from 'firebase'
import Weekday from './weekday'
import PickADay from './PickADay'
import posed, { PoseGroup } from 'react-pose';
import { Alert } from 'reactstrap'


class StrengthLog extends React.Component {
    state = {
        lifts: {
            1: {
                name: '',
                sets: {
                    1: {
                        amount: '',
                        weight: '',
                        reps: ''
                    },

                }
            }
        },
        stage: 1
    }


    addASet = (e, lift) => {
        e.preventDefault()
        var oldLift = JSON.parse(JSON.stringify(this.state.lifts[lift]))
        var newSet = parseInt(Math.max.apply(null, (Object.keys(oldLift.sets)))) + 1

        oldLift.sets[newSet] = {
            amount: '',
            weight: '',
            reps: ''
        }

        var oldLifts = JSON.parse(JSON.stringify(this.state.lifts))


        oldLifts[lift] = oldLift

        this.setState({ lifts: oldLifts }, () => {
            console.log(this.state)
        })

    }

    addALift = (e) => {
        e.preventDefault()
        var lifts = JSON.parse(JSON.stringify(this.state.lifts))

        var newLift = parseInt(Math.max.apply(null, Object.keys(lifts))) + 1

        lifts[newLift] = {
            lift: '',
            sets: {
                1: {
                    amount: '',
                    weight: '',
                    reps: ''
                }

            }
        }
        this.setState({ lifts: lifts })

    }

    changeLiftName = (e, lift) => {
        var lifts = this.state.lifts
        lifts[lift].name = e.target.value
        this.setState({ lifts: lifts }, () => { console.log(this.state) })

    }
    changeAmount = (e, lift, set, value) => {
        var lifts = this.state.lifts
        lifts[lift].sets[set][value] = e.target.value
        this.setState({ lifts: lifts }, () => { console.log(this.state) })
    }
    handleSubmit = (event) => {
        event.preventDefault()
        firebase.firestore().collection('Users').doc(this.props.picked).collection('Workouts').doc().set({ lifts: this.state.lifts, date: this.props.date })
    }


    next = () => {
        var { stage } = this.props
        this.setState({ stage: stage + 1 })
    }


    render() {

        const { stage } = this.props;
        console.log(stage)
        let First = posed.div({
            enter: {
                x: 0,
                opacity: 1,
                delay: 300,
                transition: ({ from, to }) => {
                    console.log(from)
                    console.log(to)
                    return ({
                        type: 'keyframes',
                        values: [500, to],
                        times: [0, 1],
                        duration: 1000
                    }
                    )
                }
            }
            ,
            exit: {
                x: -500,
                opacity: 0,
                transition: { duration: 500 }
            }
        });
        let Second = posed.div({
            enter: {
                x: 0,
                opacity: 1,
                delay: 300,
                transition: ({ from, to }) => {
                    console.log(from)
                    console.log(to)
                    return ({
                        type: 'keyframes',
                        values: [500, to],
                        times: [0, 1],
                        duration: 1000
                    }
                    )
                }
            }
            ,
            exit: {
                x: -500,
                opacity: 0,
                transition: { duration: 500 }
            }
        });


        return (
            <div className='container'>
                <PoseGroup>
                    {stage === 1 &&
                        [<First key="shade">
                            <PickADay
                                next={this.next}
                                handleDayChange={this.props.handleDayChange}
                                date={this.props.date} /></First>,


                        <Second key="shade1" ><Weekday pickDay={this.props.pickDay} daysPicked={this.props.daysPicked} /></Second>
                        ]}

                    <div key='button' className='row justify-content-center' style={{ margin: '20px 0' }}>
                        <button class='btn btn-outline-primary' type='submit' onClick={this.handleNext}> Next</button>
                    </div>
                </PoseGroup>


            </div >
        )
    }
}

export default StrengthLog
