import React from 'react';
import posed, { PoseGroup } from 'react-pose';


class WeekCreator extends React.Component {



    render() {
        let week = posed.div({
            enter: {
                x: 0,
                opacity: 1,
                delay: 300,
                transition: ({ from, to }) => {

                    return ({
                        type: 'keyframes',
                        values: [500, to],
                        times: [0, 1],
                        duration: 1000
                    }
                    )
                }
            }
            ,
            exit: {
                x: -500,
                opacity: 0,
                transition: { duration: 500 }
            }
        })

        let weekNumberArray = []
        for (let index = 0; index < this.props.weeks; index++) {
            weekNumberArray.push(index + 1);
        }
        let daysPicked = this.props.daysPicked.sort(function(a, b){return a - b})
        let letterDaysPicked = []
        daysPicked.forEach(day=>{
            switch (day) {
                case 1:
                    letterDaysPicked.push('Monday')
                    break;
                    case 2:
                    letterDaysPicked.push('Tuesday')
                    break;
                    case 3:
                    letterDaysPicked.push('Wednesday')
                    break;
                    case 4:
                    letterDaysPicked.push('Thursday')
                    break;
                    case 5:
                    letterDaysPicked.push('Friday')
                    break;
                    case 6:
                    letterDaysPicked.push('Saturday')
                    break;
                    case 0:
                    letterDaysPicked.push('Sunday')
                    break;
                default:
                    break;
            }
        })


        return (

            <div>
                {weekNumberArray.map((element) => {
                    return (
                        <div>
                            <div className='row justify-content-center' style={{ marginTop: '10px' }}>
                                <h3>Week {element}</h3>
                            </div>
                            <div className='row justify-content-center' style={{ marginTop: '10px' }}>
                            {letterDaysPicked.map(day=>{
                               return( <div className='col'>
                                <div className='row justify-content-center'>
                                <h3>{day}</h3>
                                </div>
                                <div className='row justify-content-center'>
                                <h3>Primary</h3>
                                </div>
                                <div className='row justify-content-center'>
                                <h3>Secondary</h3>
                                </div>
                            </div>
                               )
                            })}

                            </div>
                        </div>
                    )
                })

                }
            </div>)
    }
}

export default WeekCreator
