import React from 'react';
import posed, { PoseGroup } from 'react-pose';


class WeekCreator extends React.Component {
    state = {
        i: 0,

    }

    changeLiftName = (e,day, lift) => {
        var lifts = this.state[day].lifts
        lifts[lift].name = e.target.value
        this.setState({ [day]: lifts }, () => { console.log(this.state) })
    }
    changeAmount = (e,day, lift, set, value) => {
        var lifts = this.state[day].lifts
        lifts[lift].sets[set][value] = e.target.value
        this.setState({ lifts: lifts }, () => { console.log(this.state) })
    }

    addALift = (e,day) => {
        e.preventDefault()
        var dayLifts = JSON.parse(JSON.stringify(this.state[day]))

        var newLift = parseInt(Math.max.apply(null, Object.keys(dayLifts.lifts))) + 1

        dayLifts.lifts[newLift] = {
            lift: '',
            sets: {
                1: {
                    amount: '',
                    weight: '',
                    reps: ''
                }

            }
        }
        this.setState({ [day]: dayLifts })

    }


    addASet = (e,day, lift) => {
        e.preventDefault()
        console.log(this.state[day].lifts)

        var oldLift = JSON.parse(JSON.stringify(this.state[day].lifts[lift]))

        var newSet = parseInt(Math.max.apply(null, (Object.keys(oldLift.sets)))) + 1
        console.log(newSet)

        oldLift.sets[newSet] = {
            amount: '',
            weight: '',
            reps: ''
        }

        var oldLifts = JSON.parse(JSON.stringify(this.state[day]))

        console.log(oldLift)
        oldLifts.lifts[lift] = oldLift
        console.log(oldLifts)

        this.setState({[day]: oldLifts }, () => {
            console.log(this.state)
        })

    }




    addWeekdayState = () => {

        let daysPicked = this.props.daysPicked.sort(function (a, b) { return (a - b) })

        for (let index = 0; index < this.props.weeks; index++) {
            daysPicked.forEach(day => {
                this.setState({
                    [day]: {lifts:{
                        1: {
                            name: '',
                            sets: {
                                1: {
                                    amount: '',
                                    weight: '',
                                    reps: ''
                                },

                            }
                        }
                    }}
                })

            })

        }
    }

    componentWillMount = () => {
        this.addWeekdayState()
    }


    render() {
        let Week = posed.div({
            enter: {
                x: 0,
                opacity: 1,
                delay: 300,
                transition: ({ from, to }) => {

                    return ({
                        type: 'keyframes',
                        values: [500, to],
                        opacity: 1,
                        times: [0, 1],
                        duration: 1000
                    }
                    )
                }
            }
            ,
            exit: {
                x: 0,
                opacity: 1,
                transition: ({ from, to }) => {

                    return ({
                        type: 'keyframes',
                        values: [-500, to],
                        opacity: 1,
                        times: [0, 1],
                        duration: 1000
                    }
                    )
                }
            }
        })

        let weekNumberArray = []
        for (let index = 0; index < this.props.weeks; index++) {
            weekNumberArray.push(index + 1);
        }
        let daysPicked = this.props.daysPicked.sort(function (a, b) { return a - b })
        let letterDaysPicked = []
        daysPicked.forEach(day => {
            switch (day) {
                case 1:
                    letterDaysPicked.push({ day: 'Monday', value: 1 })
                    break;
                case 2:
                    letterDaysPicked.push({ day: 'Tuesday', value: 2 })
                    break;
                case 3:
                    letterDaysPicked.push({ day: 'Wednesday', value: 3 })
                    break;
                case 4:
                    letterDaysPicked.push({ day: 'Thursday', value: 4 })
                    break;
                case 5:
                    letterDaysPicked.push({ day: 'Friday', value: 5 })
                    break;
                case 6:
                    letterDaysPicked.push({ day: 'Saturday', value: 6 })
                    break;
                case 0:
                    letterDaysPicked.push({ day: 'Sunday', value: 0 })
                    break;
                default:
                    break;
            }
        })


        console.log(this.state)


        return (


            <div>
                <PoseGroup>
                    {weekNumberArray.map((element, index) => {

                                {index === this.state.i && <Week key={`week ${index + 1}`}>
                                    <div className='row justify-content-center' style={{ marginTop: '10px' }}>
                                        <h3 >Week {element}</h3>
                                    </div>
                                    <div className='row justify-content-center' style={{ marginTop: '10px' }}>
                                        {letterDaysPicked.map(day => {
                                            return (<div className='col' style ={{borderRight: '1px solid grey'}}>
                                                <div className='row justify-content-center'>
                                                    <h4>{day.day}</h4>
                                                </div>
                                                <div className='row justify-content-center'>
                                                    <h5 style= {{margin:'6px 5px 6px 0'}}>Lifts</h5> <button className='btn btn-sm btn-outline-primary' onClick={(event)=>{this.addALift(event,day.value)}}> Add a Lift </button>


                                                    {/*   Going through each individual lift    */}
                                                    {Object.keys(this.state[day.value].lifts).map(lift => {


                                                        return (
                                                            <div className='row' style={{ marginTop: '20px' }}>
                                                                <div className='col-sm-4 align-items-start' style={{ height: '100%' }}>
                                                                    <input name={lift} placeholder='Lift' onChange={(e) => { this.changeLiftName(e,day.value, lift) }} style={{width:'150px'}} required />
                                                                    <button className='btn btn-success' onClick={(e) => { this.addASet(e,day.value, lift) }} style={{ marginBottom: '15px' }}>Add a Set</button>
                                                                </div>
                                                            <div className='col-sm-1'></div>

                                                                {/* Going through the sets of the lift */}
                                                                <div className='col-sm-5' style={{ marginBottom: '15px' }}>
                                                                    {Object.keys(this.state[day.value].lifts[lift].sets).map(set => {
                                                                        return (
                                                                            <div className='row'>
                                                                                <input value={this.state[day.value].lifts[lift].sets.amount} placeholder='Sets' onChange={(e) => { this.changeAmount(e,day.value, lift, set, 'amount') }} style={{width:'50px',marginRight:'5px'}} required />
                                                                                <input placeholder='Reps' onChange={(e) => { this.changeAmount(e,day.value, lift, set, 'reps') }} style={{width:'50px',marginRight:'5px'}} required />
                                                                                <input placeholder='Weight' onChange={(e) => { this.changeAmount(e,day.value, lift, set, 'weight') }} style={{width:'60px',marginRight:'5px'}} required />
                                                                            </div>
                                                                        )
                                                                    })}
                                                                </div>


                                                            </div>
                                                        )

                                                                })}


                                                </div>
                                            </div>
                                            )
                                                })}

                                    </div>
                                </Week>}



                    })

                    }
                </PoseGroup>
                <div className='row justify-content-center'>
                <button className='btn btn-outline-primary' onClick={() => { this.setState({ i: this.state.i + 1 })}}> Next Week</button>
                </div>
            </div>)
    }
}

export default WeekCreator
