import React from 'react';
import posed, { PoseGroup } from 'react-pose';


class WeekCreator extends React.Component {
    state = {
        i: 0,

    }






    addWeekdayState = () => {

        let daysPicked = this.props.daysPicked.sort(function (a, b) { return (a - b) })

        for (let index = 0; index < this.props.weeks; index++) {
            daysPicked.forEach(day => {
                this.setState({
                    [day]: {lifts:{
                        name: '',
                        sets: {
                            1: {
                                amount: '',
                                weight: '',
                                reps: ''
                            },

                        }
                    }}
                })

            })

        }
    }

    componentWillMount = () => {
        this.addWeekdayState()
    }


    render() {
        let Week = posed.div({
            enter: {
                x: 0,
                opacity: 1,
                delay: 300,
                transition: ({ from, to }) => {

                    return ({
                        type: 'keyframes',
                        values: [500, to],
                        opacity: 1,
                        times: [0, 1],
                        duration: 1000
                    }
                    )
                }
            }
            ,
            exit: {
                x: 0,
                opacity: 1,
                transition: { duration: 500 }
            }
        })

        let weekNumberArray = []
        for (let index = 0; index < this.props.weeks; index++) {
            weekNumberArray.push(index + 1);
        }
        let daysPicked = this.props.daysPicked.sort(function (a, b) { return a - b })
        let letterDaysPicked = []
        daysPicked.forEach(day => {
            switch (day) {
                case 1:
                    letterDaysPicked.push({ day: 'Monday', value: 1 })
                    break;
                case 2:
                    letterDaysPicked.push({ day: 'Tuesday', value: 2 })
                    break;
                case 3:
                    letterDaysPicked.push({ day: 'Wednesday', value: 3 })
                    break;
                case 4:
                    letterDaysPicked.push({ day: 'Thursday', value: 4 })
                    break;
                case 5:
                    letterDaysPicked.push({ day: 'Friday', value: 5 })
                    break;
                case 6:
                    letterDaysPicked.push({ day: 'Saturday', value: 6 })
                    break;
                case 0:
                    letterDaysPicked.push({ day: 'Sunday', value: 0 })
                    break;
                default:
                    break;
            }
        })





        return (


            <div>
                <PoseGroup>
                    {weekNumberArray.map((element, index) => {
                        return (
                            <div key={index}>
                                {index === this.state.i && <Week key={`week ${index + 1}`}>
                                    <div className='row justify-content-center' style={{ marginTop: '10px' }}>
                                        <h3 onClick={() => { this.setState({ i: this.state.i + 1 }, () => { console.log(this.state) }) }}>Week {element}</h3>
                                    </div>
                                    <div className='row justify-content-center' style={{ marginTop: '10px' }}>
                                        {letterDaysPicked.map(day => {
                                            return (<div className='col'>
                                                <div className='row justify-content-center'>
                                                    <h4>{day.day}</h4>
                                                </div>
                                                <div className='row justify-content-center'>
                                                    <h5>Lifts</h5>


                                                    {/*   Going through each individual lift    */}
                                                    {Object.keys(this.state[day.value].lifts).map(lift => {
                                                        return (
                                                            <div className='row' style={{ marginTop: '20px' }}>
                                                                <div className='col-sm-3 align-items-start' style={{ height: '100%' }}>
                                                                    <input name={lift} placeholder='Lift' onChange={(e) => { this.changeLiftName(e, lift) }} required />
                                                                    <button className='btn btn-success' onClick={(e) => { this.addASet(e, lift) }} style={{ marginBottom: '15px' }}>Add a Set</button>
                                                                </div>


                                                                {/* Going through the sets of the lift */}
                                                                <div className='col-sm-9' style={{ marginBottom: '15px' }}>
                                                                    {Object.keys(this.state.lifts[lift].sets).map(set => {
                                                                        return (
                                                                            <div className='row'>
                                                                                <input placeholder='Sets' onChange={(e) => { this.changeAmount(e, lift, set, 'amount') }} required />
                                                                                <input placeholder='Reps' onChange={(e) => { this.changeAmount(e, lift, set, 'reps') }} required />
                                                                                <input placeholder='Weight' onChange={(e) => { this.changeAmount(e, lift, set, 'weight') }} required />
                                                                            </div>
                                                                        )
                                                                    })}
                                                                </div>



                                                            </div>
                                                        )

                                                    })}


                                                </div>
                                            </div>
                                            )
                                        })}

                                    </div>
                                </Week>}
                            </div>
                        )
                    })

                    }
                </PoseGroup>
            </div>)
    }
}

export default WeekCreator
